import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { Button, Input, Text, Header, icon } from 'react-native-elements';
import { useState, useEffect } from 'react';
import { initializeApp } from "firebase/app";
import axios from 'axios';
import FlashMessage from "react-native-flash-message";
import { showMessage, hideMessage } from "react-native-flash-message";

export default function App({ route,navigation }) {

  const [getNome,setNome] = useState();
  const [getEmail,setEmail] = useState();
  const [getTelefone,setTelefone] = useState();
  const [getId,setId] = useState();
  const [getBotao,setBotao] = useState();

  const [dados,setDados] = useState([]);

  useEffect(()=>{
    if(route.params){
      const { nome } =  route.params 
      const { email } = route.params 
      const { telefone } = route.params
      const { id } = route.params
      const { botao } = route.params

        setNome(nome);
        setEmail(email);
        setTelefone(telefone);
        setId(id);
        setBotao(botao);
    }

    }, [])

    function inserirDados(){
      axios.post('http://professornilson.com/testeservico/clientes'
      , {
      nome: getNome,
      telefone: getTelefone,
      email: getEmail
      }).then(function (response) {
        setNome('');
        setEmail('');
        setTelefone('');
        showMessage({
          message: "Registro Adicionado com Sucesso!!",
          type: "success",
        });
      console.log(response);
      }).catch(function (error) {
      console.log(error);
      });
      }

    function listarDados(){

        navigation.navigate('CadastroScreen')

      }


  return (
    <View style={styles.container}>

      <Text h1></Text>
      <Input
        placeholder="Nome"
        leftIcon={{ type: 'font-awesome', name: '' }}
        onChangeText={value => setNome(value)}

        />

      <Input
        placeholder="Email"
        leftIcon={{ type: 'font-awesome', name: '' }}
        onChangeText={value => setEmail(value)}
        keyboardType="email-address"
        />

      <Input
        placeholder="Telefone"
        leftIcon={{ type: 'font-awesome', name: '' }}
        onChangeText={value => setTelefone(value)}
        />

{ !getId ? (
            <Button style={{paddingTop:20}}
              title="Salvar"

              onPress={() => inserirDados()}
            />
            ):null}

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
