import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { Button, Input, Text, Header, icon } from 'react-native-elements';
import { useState, useEffect } from 'react';
import { initializeApp } from "firebase/app";
import axios from 'axios';

export default function App({route,navigation}) {

  const [getnome, setNome] = useState('')
  const [getemail, setEmail] = useState('')
  const [getphone, setPhone] = useState('')


  useEffect(()=>{
    if(route.params){
      const { nome } =  route.params 
      const { cpf } = route.params 
      const { telefone } = route.params
      const { id } = route.params
      const { botao } = route.params

        setNome(nome);
        setPhone(telefone);
        setCpf(cpf);
        setId(id);
        setBotao(botao);
    }

    }, [])




  function inserirDados(){

      const result =  axios.post('http://professornilson.com/testeservico/clientes', {
          nome: getNome,
          cpf: getCpf,
          telefone: getphone
        })
        .then(function (response) {
          setNome('');
          setCpf('');
          setPhone('');
          setId('');
          showMessage({
            message: "Registro Adicionado com Sucesso!!",
            type: "success",
          });
          //navigation.navigate('Cadastro')
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        });

  }


  return (
    <View style={styles.container}>
    <header
      leftComponent={{ icon: 'menu', color:  '#afff' }}
      centerComponent={{ text: 'Contato', style: { color: '#afff '} }}
      rightComponent={{ icon: 'home', color: '#afff' }}

    /> 

      <Text h1></Text>
      <Input
        placeholder="Nome"
        leftIcon={{ type: 'font-awesome', name: '' }}
        onChangeText={value => setNome(value)}

        />

      <Input
        placeholder="Email"
        leftIcon={{ type: 'font-awesome', name: '' }}
        onChangeText={value => setEmail(value)}
        keyboardType="email-address"
        />

      <Input
        placeholder="Telefone"
        leftIcon={{ type: 'font-awesome', name: '' }}
        onChangeText={value => setPhone(value)}
        />

<Button
                title={'Salvar'}
                containerStyle={{
                  width: 200,
                  marginHorizontal: 50,
                  marginVertical: 10,
                }}
              />


    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
